INSERT INTO `wp_postmeta` VALUES("1647", "1762", "wp_travel_engine_setting", "a:19:{s:9:\"trip_code\";s:8:\"WTE-1748\";s:13:\"trip_duration\";s:1:\"2\";s:18:\"trip_duration_unit\";s:4:\"days\";s:20:\"trip_duration_nights\";s:1:\"2\";s:17:\"trip_cut_off_time\";s:0:\"\";s:17:\"trip_cut_off_unit\";s:4:\"days\";s:16:\"trip_minimum_pax\";s:0:\"\";s:16:\"trip_maximum_pax\";s:0:\"\";s:22:\"overview_section_title\";s:8:\"Overview\";s:11:\"tab_content\";a:1:{s:10:\"1_wpeditor\";s:0:\"\";}s:21:\"trip_highlights_title\";s:0:\"\";s:20:\"trip_itinerary_title\";s:9:\"Itinerary\";s:9:\"itinerary\";a:2:{s:15:\"itinerary_title\";a:2:{i:1;s:17:\"Visites iconiques\";i:2;s:19:\"Culture et Shopping\";}s:17:\"itinerary_content\";a:2:{i:1;s:895:\"Tower of London : Commencez votre journée par la visite de la Tour de Londres, un site historique emblématique qui abrite les joyaux de la Couronne britannique.\r\n\r\nTower Bridge : À proximité, admirez la vue sur la Tamise depuis le pont emblématique de Tower Bridge.\r\n\r\nBorough Market : Dirigez-vous vers Borough Market pour déjeuner. C\'est l\'un des marchés alimentaires les plus renommés de Londres, offrant une variété de plats délicieux.\r\n\r\nShakespeare\'s Globe Theatre : Explorez le quartier de South Bank et visitez le Shakespeare\'s Globe Theatre, une reconstitution du théâtre élisabéthain qui propose des visites fascinantes.\r\n\r\nTate Modern : Terminez votre journée artistique en visitant la Tate Modern, qui abrite une collection contemporaine d\'art international.\r\n\r\nMillennium Bridge : Traversez le Millennium Bridge pour une vue magnifique sur la cathédrale Saint-Paul.\";i:2;s:1110:\"British Museum : Commencez votre deuxième jour au British Museum, où vous pourrez découvrir des trésors historiques du monde entier.\r\n\r\nCovent Garden : Promenez-vous jusqu\'à Covent Garden pour découvrir ses boutiques, artistes de rue et cafés animés.\r\n\r\nBritish Library : Découvrez la British Library et ses expositions fascinantes, y compris des manuscrits historiques et des trésors littéraires.\r\n\r\nRegent\'s Park : Profitez d\'une pause détente à Regent\'s Park, idéal pour une promenade tranquille ou un pique-nique.\r\n\r\nCamden Town : Terminez votre week-end à Londres en explorant le quartier alternatif de Camden Town. Parcourez les marchés éclectiques, découvrez des boutiques uniques et savourez une cuisine internationale dans les stands de nourriture du marché.\r\n\r\nPrimrose Hill : Pour une vue panoramique époustouflante de la ville, montez jusqu\'à Primrose Hill, situé à proximité. C\'est un endroit idéal pour admirer le coucher de soleil sur Londres.\r\n\r\nIl est déjà temps de repartir... votre vol de nuit vous permettra de décompresser et de vous reposer avant la reprise...\";}}s:18:\"cost_tab_sec_title\";s:0:\"\";s:4:\"cost\";a:4:{s:14:\"includes_title\";s:0:\"\";s:13:\"cost_includes\";s:0:\"\";s:14:\"excludes_title\";s:0:\"\";s:13:\"cost_excludes\";s:0:\"\";}s:16:\"trip_facts_title\";s:0:\"\";s:17:\"map_section_title\";s:0:\"\";s:3:\"map\";a:2:{s:9:\"image_url\";s:0:\"\";s:6:\"iframe\";s:0:\"\";}s:17:\"faq_section_title\";s:0:\"\";}");
