INSERT INTO `wp_postmeta` VALUES("1711", "1771", "wp_travel_engine_setting", "a:19:{s:9:\"trip_code\";s:8:\"WTE-1748\";s:13:\"trip_duration\";s:1:\"2\";s:18:\"trip_duration_unit\";s:4:\"days\";s:20:\"trip_duration_nights\";s:1:\"2\";s:17:\"trip_cut_off_time\";s:0:\"\";s:17:\"trip_cut_off_unit\";s:4:\"days\";s:16:\"trip_minimum_pax\";s:0:\"\";s:16:\"trip_maximum_pax\";s:0:\"\";s:22:\"overview_section_title\";s:8:\"Overview\";s:11:\"tab_content\";a:1:{s:10:\"1_wpeditor\";s:0:\"\";}s:21:\"trip_highlights_title\";s:0:\"\";s:20:\"trip_itinerary_title\";s:9:\"Itinerary\";s:9:\"itinerary\";a:2:{s:15:\"itinerary_title\";a:2:{i:1;s:39:\"Découverte des Trésors Architecturaux\";i:2;s:38:\"Plongée dans la Culture et la Cuisine\";}s:17:\"itinerary_content\";a:2:{i:1;s:761:\"Matin :\r\nCommencez votre journée par la visite de la Sagrada Familia, l\'œuvre emblématique d\'Antoni Gaudí. Admirez l\'architecture fascinante de cette basilique inachevée, où la lumière filtre à travers les vitraux colorés, créant une atmosphère divine.\r\n\r\nAprès-midi :\r\nExplorez le quartier gothique, où l\'histoire médiévale se dévoile à chaque coin de rue. Visitez la cathédrale de Barcelone et déambulez dans les ruelles pavées du Barri Gòtic, découvrant des places pittoresques et des boutiques d\'artisanat.\r\n\r\nSoir :\r\nDétendez-vous sur la plage de Barceloneta, profitez du soleil méditerranéen et promenez-vous sur le front de mer animé. Terminez la journée par un dîner de fruits de mer dans l\'un des restaurants en bord de mer.\";i:2;s:718:\"Matin :\r\nCommencez votre journée en explorant le marché animé de La Boqueria, où les étals débordent de produits frais locaux. Imprégnez-vous de l\'atmosphère vibrante et goûtez à des spécialités catalanes.\r\n\r\nAprès-midi :\r\nVisitez le quartier de l\'Eixample pour découvrir les œuvres modernistes de Gaudí, notamment la Casa Batlló et la Casa Milà. Flânez le long du Passeig de Gràcia, une avenue élégante bordée de boutiques de créateurs.\r\n\r\nSoir :\r\nPour une expérience nocturne, dirigez-vous vers le quartier El Born. Dînez dans l\'un des nombreux restaurants à tapas. Il est déjà temps de repartir... votre vol de nuit vous permettra de décompresser et de vous reposer avant la reprise...\";}}s:18:\"cost_tab_sec_title\";s:0:\"\";s:4:\"cost\";a:4:{s:14:\"includes_title\";s:0:\"\";s:13:\"cost_includes\";s:0:\"\";s:14:\"excludes_title\";s:0:\"\";s:13:\"cost_excludes\";s:0:\"\";}s:16:\"trip_facts_title\";s:0:\"\";s:17:\"map_section_title\";s:0:\"\";s:3:\"map\";a:2:{s:9:\"image_url\";s:0:\"\";s:6:\"iframe\";s:0:\"\";}s:17:\"faq_section_title\";s:0:\"\";}");
