INSERT INTO `wp_postmeta` VALUES("1878", "1789", "wp_travel_engine_setting", "a:21:{s:15:\"trip_prev_price\";s:4:\"2000\";s:10:\"trip_price\";s:4:\"1400\";s:13:\"trip_duration\";s:1:\"7\";s:20:\"trip_duration_nights\";s:1:\"6\";s:11:\"tab_content\";a:1:{s:10:\"1_wpeditor\";s:721:\"Au-delà du ski, explorez le village pittoresque de Valloire, imprégné de charme savoyard, avec ses ruelles pavées et ses chalets en bois. Les moments de détente se vivront au coin du feu dans les chalets douillets, où les rires résonneront après une journée bien remplie sur les pistes.\r\n\r\nValloire, avec son panorama époustouflant et son ambiance accueillante, promet une semaine en famille mémorable, où la joie de la montagne et la complicité familiale seront les maîtres-mots de cette escapade hivernale.\r\n\r\nPour informations les forfaits de ski et écoles de ski (pour les plus petits), se déroulent de 9h à 12h car le circuit a été pensé pour se retrouver en famille les après-midi et les soirs.\";}s:9:\"itinerary\";a:2:{s:15:\"itinerary_title\";a:7:{i:1;s:20:\"Arrivée à Valloire\";i:2;s:11:\"Tout schuss\";i:3;s:16:\"Ski, moment cozy\";i:4;s:22:\"Ski, Chien de traineau\";i:5;s:27:\"Ski, Raquettes, Faune-flore\";i:6;s:21:\"Ski, Souvenirs, Quizz\";i:7;s:14:\"Fin du Séjour\";}s:17:\"itinerary_content\";a:7:{i:1;s:418:\"Midi : Arrivée autonome à Valloire + checking chalet et déjeuner servi à domicile\r\n\r\nAprès-midi : Forfait ski familiale remis et/ou inscription école de ski (pour les plus petits).\r\nLocations de vêtements et matériels, si vous n\'avez pas les votre, et découverte de la station et différents services.\r\n\r\nSoirée : Retour à l\'heure que vous souhaitez et dîner dans le restaurant de la résidence de chalets.\";i:2;s:224:\"Matin : Ski et/ou école de ski toute la matinée et déjeuner servi à domicile.\r\n\r\nAprès-midi : Patinoire + Luge.\r\n\r\nSoirée : Retour à l\'heure que vous souhaitez et dîner dans le restaurant de la résidence de chalets.\";i:3;s:368:\"Matin : Ski et/ou école de ski toute la matinée et déjeuner servi à domicile.\r\n\r\nAprès-midi : Jacuzzi familial privatif à votre disposition pour ce troisième jour (2h).\r\n+ Accès à la salle de jeux (flippers, table de ping-pong, table de jeux de cartes (2h).\r\n\r\nSoirée : Soirée raclette (option végétarienne) dans le restaurant de la résidence de chalets.\";i:4;s:256:\"Matin : Ski et/ou école de ski toute la matinée et déjeuner servi à domicile.\r\n\r\nAprès-midi : Chien de traineau familial (balade de 2h) + Luge.\r\n\r\nSoirée : Retour à l\'heure que vous souhaitez et dîner dans le restaurant de la résidence de chalets.\";i:5;s:372:\"Matin : Ski et/ou école de ski toute la matinée et déjeuner servi à domicile.\r\n\r\nAprès-midi : Rando-raquettes sur les traces des animaux de la montagne, observations de la flore avec notre guide nature spécialise de la faune et la flore en milieu alpin. (3h)\r\n\r\nSoirée : Retour à l\'heure que vous souhaitez et dîner dans le restaurant de la résidence de chalets.\";i:6;s:415:\"Matin : Dernier jour de ski et/ou école de ski toute la matinée et déjeuner servi à domicile.\r\n\r\nAprès-midi : Temps libre, achats et visites du village de Valloire + déposer les différentes affaires et matériels de locations.\r\n\r\nSoirée : Pour ce dernier soir, soirée fondue (option végétarienne) dans le restaurant de la résidence de chalets + animation quizz sur le thème de la nature à la montagne.\";i:7;s:79:\"Matin : Nettoyage sommaire de votre chalet pour votre départ prévu avant 12h.\";}}s:4:\"cost\";a:4:{s:14:\"includes_title\";s:29:\"Le coût du voyage comprend :\";s:13:\"cost_includes\";s:220:\"Déjeuner et Dîner\r\nFrais d\'hébergement (+ chauffage, électricité, Wifi)\r\nForfait + école de ski (le matin de 9h-12h)\r\nAccès limité à nos services (jacuzzi et salle de jeux)\r\nTrousse médicale de premiers secours\";s:14:\"excludes_title\";s:36:\"Le coût du voyage ne comprend pas :\";s:13:\"cost_excludes\";s:138:\"Assurance médicale\r\nDépenses personnelles (courses, factures de bar, téléphone, blanchisserie, friandises etc.)\r\nFrais de bus ou taxis\";}s:9:\"trip_code\";s:7:\"WTE-279\";s:18:\"trip_duration_unit\";s:4:\"days\";s:17:\"trip_cut_off_time\";s:0:\"\";s:17:\"trip_cut_off_unit\";s:4:\"days\";s:16:\"trip_minimum_pax\";s:0:\"\";s:16:\"trip_maximum_pax\";s:0:\"\";s:22:\"overview_section_title\";s:0:\"\";s:21:\"trip_highlights_title\";s:0:\"\";s:20:\"trip_itinerary_title\";s:0:\"\";s:18:\"cost_tab_sec_title\";s:0:\"\";s:16:\"trip_facts_title\";s:0:\"\";s:17:\"map_section_title\";s:0:\"\";s:3:\"map\";a:2:{s:9:\"image_url\";s:0:\"\";s:6:\"iframe\";s:0:\"\";}s:17:\"faq_section_title\";s:0:\"\";}");
