INSERT INTO `wp_postmeta` VALUES("1878", "1789", "wp_travel_engine_setting", "a:21:{s:15:\"trip_prev_price\";s:4:\"2000\";s:10:\"trip_price\";s:4:\"1400\";s:13:\"trip_duration\";s:1:\"7\";s:20:\"trip_duration_nights\";s:1:\"6\";s:11:\"tab_content\";a:1:{s:10:\"1_wpeditor\";s:721:\"Au-del du ski, explorez le village pittoresque de Valloire, imprgn de charme savoyard, avec ses ruelles paves et ses chalets en bois. Les moments de dtente se vivront au coin du feu dans les chalets douillets, o les rires rsonneront aprs une journe bien remplie sur les pistes.\r\n\r\nValloire, avec son panorama poustouflant et son ambiance accueillante, promet une semaine en famille mmorable, o la joie de la montagne et la complicit familiale seront les matres-mots de cette escapade hivernale.\r\n\r\nPour informations les forfaits de ski et coles de ski (pour les plus petits), se droulent de 9h  12h car le circuit a t pens pour se retrouver en famille les aprs-midi et les soirs.\";}s:9:\"itinerary\";a:2:{s:15:\"itinerary_title\";a:7:{i:1;s:20:\"Arrive  Valloire\";i:2;s:11:\"Tout schuss\";i:3;s:16:\"Ski, moment cozy\";i:4;s:22:\"Ski, Chien de traineau\";i:5;s:27:\"Ski, Raquettes, Faune-flore\";i:6;s:21:\"Ski, Souvenirs, Quizz\";i:7;s:14:\"Fin du Sjour\";}s:17:\"itinerary_content\";a:7:{i:1;s:418:\"Midi : Arrive autonome  Valloire + checking chalet et djeuner servi  domicile\r\n\r\nAprs-midi : Forfait ski familiale remis et/ou inscription cole de ski (pour les plus petits).\r\nLocations de vtements et matriels, si vous n\'avez pas les votre, et dcouverte de la station et diffrents services.\r\n\r\nSoire : Retour  l\'heure que vous souhaitez et dner dans le restaurant de la rsidence de chalets.\";i:2;s:224:\"Matin : Ski et/ou cole de ski toute la matine et djeuner servi  domicile.\r\n\r\nAprs-midi : Patinoire + Luge.\r\n\r\nSoire : Retour  l\'heure que vous souhaitez et dner dans le restaurant de la rsidence de chalets.\";i:3;s:368:\"Matin : Ski et/ou cole de ski toute la matine et djeuner servi  domicile.\r\n\r\nAprs-midi : Jacuzzi familial privatif  votre disposition pour ce troisime jour (2h).\r\n+ Accs  la salle de jeux (flippers, table de ping-pong, table de jeux de cartes (2h).\r\n\r\nSoire : Soire raclette (option vgtarienne) dans le restaurant de la rsidence de chalets.\";i:4;s:256:\"Matin : Ski et/ou cole de ski toute la matine et djeuner servi  domicile.\r\n\r\nAprs-midi : Chien de traineau familial (balade de 2h) + Luge.\r\n\r\nSoire : Retour  l\'heure que vous souhaitez et dner dans le restaurant de la rsidence de chalets.\";i:5;s:372:\"Matin : Ski et/ou cole de ski toute la matine et djeuner servi  domicile.\r\n\r\nAprs-midi : Rando-raquettes sur les traces des animaux de la montagne, observations de la flore avec notre guide nature spcialise de la faune et la flore en milieu alpin. (3h)\r\n\r\nSoire : Retour  l\'heure que vous souhaitez et dner dans le restaurant de la rsidence de chalets.\";i:6;s:415:\"Matin : Dernier jour de ski et/ou cole de ski toute la matine et djeuner servi  domicile.\r\n\r\nAprs-midi : Temps libre, achats et visites du village de Valloire + dposer les diffrentes affaires et matriels de locations.\r\n\r\nSoire : Pour ce dernier soir, soire fondue (option vgtarienne) dans le restaurant de la rsidence de chalets + animation quizz sur le thme de la nature  la montagne.\";i:7;s:79:\"Matin : Nettoyage sommaire de votre chalet pour votre dpart prvu avant 12h.\";}}s:4:\"cost\";a:4:{s:14:\"includes_title\";s:29:\"Le cot du voyage comprend :\";s:13:\"cost_includes\";s:220:\"Djeuner et Dner\r\nFrais d\'hbergement (+ chauffage, lectricit, Wifi)\r\nForfait + cole de ski (le matin de 9h-12h)\r\nAccs limit  nos services (jacuzzi et salle de jeux)\r\nTrousse mdicale de premiers secours\";s:14:\"excludes_title\";s:36:\"Le cot du voyage ne comprend pas :\";s:13:\"cost_excludes\";s:138:\"Assurance mdicale\r\nDpenses personnelles (courses, factures de bar, tlphone, blanchisserie, friandises etc.)\r\nFrais de bus ou taxis\";}s:9:\"trip