INSERT INTO `wp_posts` VALUES("1713", "1", "2024-01-09 14:39:20", "2024-01-09 14:39:20", "{\n    \"travel-agency::banner_title\": {\n        \"value\": \"Parcourez le Monde\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-01-09 14:18:47\"\n    },\n    \"travel-agency::banner_subtitle\": {\n        \"value\": \"Explorez les tr\\u00e9sors cach\\u00e9s du monde avec nos offres exclusives. Des plages paradisiaques aux montagnes majestueuses, laissez-vous inspirer par des exp\\u00e9riences de voyage inoubliables.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-01-09 14:19:47\"\n    },\n    \"travel-agency::activities_title\": {\n        \"value\": \"Activit\\u00e9s d\'aventure\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-01-09 14:26:07\"\n    },\n    \"travel-agency::activities_desc\": {\n        \"value\": \" Ces activit\\u00e9s ne sont pas seulement des d\\u00e9fis physiques, mais aussi des opportunit\\u00e9s de connecter avec la nature, de repousser ses limites et de cr\\u00e9er des souvenirs qui durent toute une vie. Que vous soyez un amateur de sensations fortes chevronn\\u00e9 ou que vous cherchiez simplement \\u00e0 ajouter une dose d\'excitation \\u00e0 votre voyage, les activit\\u00e9s d\'aventure sont une invitation \\u00e0 sortir de votre zone de confort et \\u00e0 embrasser l\'extraordinaire.\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-01-09 14:28:07\"\n    },\n    \"travel-agency::popular_title\": {\n        \"value\": \"Green tour\",\n        \"type\": \"theme_mod\",\n        \"user_id\": 1,\n        \"date_modified_gmt\": \"2024-01-09 14:34:22\"\n    },\n    \"travel-agency::popular_desc\": {\n        \"value\": \"Le \\\"Green Tour\\\" offre une escapade unique pour les amoureux de la nature et les passionn\\u00e9s d\'\\u00e9cotourisme. Cette